import React, { useState, useEffect } from "react";
import { AppBar, Toolbar, Typography, Button, Container, Box, Dialog, DialogTitle, DialogContent, TextField, DialogActions, Menu, MenuItem, ToggleButton, ToggleButtonGroup, IconButton, Table, TableHead, TableBody, TableRow, TableCell, Modal, Backdrop, Fade } from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import RemoveIcon from "@mui/icons-material/Remove";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [user, setUser] = useState({ name: "", role: "", email: "" });
  const [pipelines, setPipelines] = useState([]);
  const [profileOpen, setProfileOpen] = useState(false);
  const [pipelineStages, setPipelineStages] = useState(1);
  const [isParallel, setIsParallel] = useState(false);
  const [anchorEl, setAnchorEl] = useState(null);
  const [openStageModal, setOpenStageModal] = useState(false);
  const [selectedPipelineStages, setSelectedPipelineStages] = useState([]);
  const navigate = useNavigate();

  const user_id = localStorage.getItem("user_id");

  useEffect(() => {
    if (!user_id) {
      console.error("User ID not found! Redirecting to login.");
      navigate("/login");
      return;
    }
    fetchUserProfile();
    fetchUserPipelines();
  }, [user_id]);

  const fetchUserProfile = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/user?user_id=${user_id}`);
      setUser(response.data);
    } catch (error) {
      console.error("Failed to fetch user profile", error);
    }
  };

  const fetchUserPipelines = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/pipelines?user_id=${user_id}`);
      setPipelines(response.data);
    } catch (error) {
      console.error("Failed to fetch pipelines", error);
    }
  };

  const handleProfileSave = async () => {
    try {
      await axios.put(`http://localhost:8080/user/${user_id}`, {
        name: user.name,
        role: user.role,
      });
      setProfileOpen(false);
    } catch (error) {
      console.error("Failed to update profile", error);
    }
  };

  const handlePipelineAction = async (pipelineId, status) => {
    try {
      if (status === "Running") {
        await axios.post(`http://localhost:8080/pipelines/${pipelineId}/cancel`);
      } else {
        await axios.post(`http://localhost:8080/pipelines/${pipelineId}/start`, {
          user_id: user_id,
          input: {
            raw_material: "Steel",
            quantity: 100,
          },
          is_parallel: true,
        });
      }
      fetchUserPipelines();
    } catch (error) {
      console.error("Failed to update pipeline status", error);
    }
  };

  const handleViewStages = async (pipelineId) => {
    try {
      const response = await axios.get(`http://localhost:8080/pipelines/${pipelineId}/stages`);
      setSelectedPipelineStages(response.data);
      setOpenStageModal(true);
    } catch (error) {
      console.error("Failed to fetch pipeline stages", error);
    }
  };

  return (
    <Container maxWidth="md">
      <AppBar position="static">
        <Toolbar sx={{ display: "flex", justifyContent: "space-between" }}>
          <Typography variant="h6">Dashboard</Typography>
          <Button color="inherit" onClick={(e) => setAnchorEl(e.currentTarget)}>Profile</Button>
          <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={() => setAnchorEl(null)}>
            <MenuItem onClick={() => setProfileOpen(true)}>Edit Profile</MenuItem>
          </Menu>
        </Toolbar>
      </AppBar>
      <Box sx={{ textAlign: "center", mt: 5, p: 4, boxShadow: 3, borderRadius: 2 }}>
        <Typography variant="h4">Welcome, {user.name || "User"}</Typography>
      </Box>
      <Box sx={{ mt: 5, p: 3, boxShadow: 3, borderRadius: 2 }}>
        <Typography variant="h5" sx={{ mb: 2 }}>Your Pipelines</Typography>
        {pipelines.length > 0 ? (
          <Table sx={{ minWidth: 500, border: "1px solid #ccc", borderRadius: "8px" }}>
            <TableHead>
              <TableRow sx={{ backgroundColor: "#f5f5f5" }}>
                <TableCell><strong>Pipeline ID</strong></TableCell>
                <TableCell><strong>Status</strong></TableCell>
                <TableCell><strong>Action</strong></TableCell>
                <TableCell><strong>View Stages</strong></TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {pipelines.map((pipeline) => (
                <TableRow key={pipeline.PipelineID}>
                  <TableCell>{pipeline.PipelineID}</TableCell>
                  <TableCell>
                    <Typography sx={{ fontWeight: "bold", color: pipeline.Status === "Running" ? "green" : "gray" }}>
                      {pipeline.Status}
                    </Typography>
                  </TableCell>
                  <TableCell>
                    <Button variant="contained" color={pipeline.Status === "Running" ? "error" : "primary"} onClick={() => handlePipelineAction(pipeline.PipelineID, pipeline.Status)}>
                      {pipeline.Status === "Running" ? "Cancel Pipeline" : "Start Pipeline"}
                    </Button>
                  </TableCell>
                  <TableCell>
                    <Button variant="outlined" color="secondary" onClick={() => handleViewStages(pipeline.PipelineID)}>View Stages</Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        ) : (
          <Typography>No pipelines created.</Typography>
        )}
      </Box>
    </Container>
  );
};

export default Dashboard;
