import React, { useState, useEffect } from "react";
import { AppBar, Toolbar, Typography, Button, Container, Box, Dialog, DialogTitle, DialogContent, TextField, DialogActions, Menu, MenuItem } from "@mui/material";
import axios from "axios";

const Dashboard = () => {
  const [user, setUser] = useState({ name: "", role: "", email: "" });
  const [pipelines, setPipelines] = useState([]);
  const [profileOpen, setProfileOpen] = useState(false);
  const [pipelineStages, setPipelineStages] = useState("");
  const [isParallel, setIsParallel] = useState(false);
  const [anchorEl, setAnchorEl] = useState(null);

  useEffect(() => {
    fetchUserProfile();
    fetchUserPipelines();
  }, []);

  const fetchUserProfile = async () => {
    try {
      const response = await axios.get("http://localhost:8080/user");
      setUser(response.data);
    } catch (error) {
      console.error("Failed to fetch user profile", error);
    }
  };

  const fetchUserPipelines = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/pipelines?user_id=${user.email}`);
      setPipelines(response.data);
    } catch (error) {
      console.error("Failed to fetch pipelines", error);
    }
  };

  const handleProfileSave = async () => {
    try {
      await axios.put("http://localhost:8080/user", {
        name: user.name,
        role: user.role,
        email: user.email,
      });
      setProfileOpen(false);
    } catch (error) {
      console.error("Failed to update profile", error);
    }
  };

  const handleCreatePipeline = async () => {
    try {
      await axios.post("http://localhost:8080/pipelines", {
        stages: parseInt(pipelineStages),
        is_parallel: isParallel,
        user_id: user.email,
      });
      fetchUserPipelines();
    } catch (error) {
      console.error("Failed to create pipeline", error);
    }
  };

  return (
    <Container maxWidth="md">
      <AppBar position="static">
        <Toolbar sx={{ display: "flex", justifyContent: "space-between" }}>
          <Typography variant="h6">Dashboard</Typography>
          <Button color="inherit" onClick={(e) => setAnchorEl(e.currentTarget)}>Profile</Button>
          <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={() => setAnchorEl(null)}>
            <MenuItem onClick={() => setProfileOpen(true)}>Edit Profile</MenuItem>
          </Menu>
        </Toolbar>
      </AppBar>
      <Box sx={{ textAlign: "center", mt: 5, p: 4, boxShadow: 3, borderRadius: 2 }}>
        <Typography variant="h4">Welcome, {user.name || "User"}</Typography>
      </Box>

      <Box sx={{ mt: 5, p: 3, boxShadow: 3, borderRadius: 2 }}>
        <Typography variant="h5">Your Pipelines</Typography>
        {pipelines.length > 0 ? (
          <ul>
            {pipelines.map((pipeline) => (
              <li key={pipeline.id}>{pipeline.id} - {pipeline.status}</li>
            ))}
          </ul>
        ) : (
          <Typography>No pipelines created.</Typography>
        )}
      </Box>

      <Box sx={{ mt: 5, p: 3, boxShadow: 3, borderRadius: 2 }}>
        <Typography variant="h5">Create New Pipeline</Typography>
        <TextField
          label="Number of Stages"
          type="number"
          fullWidth
          margin="normal"
          value={pipelineStages}
          onChange={(e) => setPipelineStages(e.target.value)}
        />
        <Button variant="contained" color="primary" onClick={() => setIsParallel(!isParallel)}>
          {isParallel ? "Parallel" : "Sequential"}
        </Button>
        <Button variant="contained" color="secondary" sx={{ mt: 2 }} onClick={handleCreatePipeline}>
          Create Pipeline
        </Button>
      </Box>

      <Dialog open={profileOpen} onClose={() => setProfileOpen(false)}>
        <DialogTitle>Edit Profile</DialogTitle>
        <DialogContent>
          <TextField
            label="Name"
            fullWidth
            margin="normal"
            value={user.name}
            onChange={(e) => setUser({ ...user, name: e.target.value })}
          />
          <TextField
            label="Role"
            fullWidth
            margin="normal"
            value={user.role}
            onChange={(e) => setUser({ ...user, role: e.target.value })}
          />
          <TextField
            label="Email"
            fullWidth
            margin="normal"
            value={user.email}
            disabled
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setProfileOpen(false)}>Cancel</Button>
          <Button onClick={handleProfileSave} color="primary">Save</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Dashboard;
