import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, useNavigate } from "react-router-dom";
import { TextField, Button, Typography, Container, Box, AppBar, Toolbar, Select, MenuItem } from "@mui/material";
import axios from "axios";

const LoginPage = () => {
  const [apiType, setApiType] = useState("rest");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleLogin = async () => {
    setMessage("");
    if (password.length < 6) {
      setMessage("Password must be at least 6 characters long.");
      return;
    }
    if (apiType === "rest") {
      try {
        await axios.post("http://localhost:8080/login", { email, password });
        setMessage("Login successful!");
      } catch (error) {
        setMessage("Login failed. Please check your credentials.");
      }
    } else {
      const grpcurl = `grpcurl -plaintext -d '{"email": "${email}", "password": "${password}"}' localhost:50051 auth.AuthService/Login`;
      console.log("Run this gRPC command manually:", grpcurl);
      setMessage("Check console for gRPC login command.");
    }
  };

  return (
    <Container maxWidth="sm">
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>Welcome</Typography>
          <Button color="inherit" onClick={() => navigate("/register")}>Register</Button>
        </Toolbar>
      </AppBar>
      <Box sx={{ textAlign: "center", mt: 5, p: 4, boxShadow: 3, borderRadius: 2, bgcolor: "white" }}>
        <Typography variant="h4" gutterBottom>Login</Typography>
        <Select value={apiType} onChange={(e) => setApiType(e.target.value)} fullWidth>
          <MenuItem value="rest">REST API</MenuItem>
          <MenuItem value="grpc">gRPC</MenuItem>
        </Select>
        <TextField label="Email" fullWidth margin="normal" value={email} onChange={(e) => setEmail(e.target.value)} />
        <TextField label="Password" type="password" fullWidth margin="normal" value={password} onChange={(e) => setPassword(e.target.value)} />
        <Button variant="contained" color="primary" fullWidth onClick={handleLogin} sx={{ mt: 2 }}>Login</Button>
        {message && <Typography sx={{ mt: 2, color: "red" }}>{message}</Typography>}
      </Box>
    </Container>
  );
};

const RegisterPage = () => {
  const [apiType, setApiType] = useState("rest");
  const [name, setName] = useState("");
  const [role, setRole] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleRegister = async () => {
    setMessage("");
    if (password.length < 6) {
      setMessage("Password must be at least 6 characters long.");
      return;
    }
    if (apiType === "rest") {
      try {
        await axios.post("http://localhost:8080/register", { name, role, email, password });
        setMessage("Registration successful! Please check your email.");
      } catch (error) {
        setMessage("Registration failed. Please try again.");
      }
    } else {
      const grpcurl = `grpcurl -plaintext -d '{"name": "${name}", "role": "${role}", "email": "${email}", "password": "${password}"}' localhost:50051 auth.AuthService/Register`;
      console.log("Run this gRPC command manually:", grpcurl);
      setMessage("Open your email and click the link to authenticate.");
    }
  };

  return (
    <Container maxWidth="sm">
      <AppBar position="static">
        <Toolbar>
          <Button color="inherit" onClick={() => navigate("/")}>Back to Login</Button>
        </Toolbar>
      </AppBar>
      <Box sx={{ textAlign: "center", mt: 5, p: 4, boxShadow: 3, borderRadius: 2, bgcolor: "white" }}>
        <Typography variant="h4" gutterBottom>Register</Typography>
        <Select value={apiType} onChange={(e) => setApiType(e.target.value)} fullWidth>
          <MenuItem value="rest">REST API</MenuItem>
          <MenuItem value="grpc">gRPC</MenuItem>
        </Select>
        <TextField label="Name" fullWidth margin="normal" value={name} onChange={(e) => setName(e.target.value)} />
        <TextField label="Role" fullWidth margin="normal" value={role} onChange={(e) => setRole(e.target.value)} />
        <TextField label="Email" fullWidth margin="normal" value={email} onChange={(e) => setEmail(e.target.value)} />
        <TextField label="Password" type="password" fullWidth margin="normal" value={password} onChange={(e) => setPassword(e.target.value)} />
        <Button variant="contained" color="primary" fullWidth onClick={handleRegister} sx={{ mt: 2 }}>Register</Button>
        {message && <Typography sx={{ mt: 2, color: "red" }}>{message}</Typography>}
      </Box>
    </Container>
  );
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<LoginPage />} />
        <Route path="/register" element={<RegisterPage />} />
      </Routes>
    </Router>
  );
};

export default App;
